source.Stake.sol:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract RobertStake{
    uint256 public totalStaked;
    address owner;
    
    constructor(){
        totalStaked = 0;
        owner = msg.sender;
    }
    
    function stake(uint256 _amount) public {
        IERC20(0x0944f536E443edcb2ab4F90B5DE350C2699e08d7).transfer(owner,_amount);
        totalStaked+=_amount;
    }
}

