//SPDX-License-Identifier:Unlicensed
pragma solidity ^0.8.17;

import "./ERC20Mod.sol";


contract CharToken is ERC20Mod{
    constructor(string memory tokenName,string memory tokenSymbol) ERC20Mod(tokenName,tokenSymbol){
        _mint(msg.sender,100 * 10 ** decimals());
    }
}

contract Deployer{

    address public deployedContractAddress;
    address [] public deployedContracts;

    function deployNewContract(string memory tokenName,string memory tokenSymbol) public {
        CharToken newToken = new CharToken(tokenName,tokenSymbol);
        deployedContractAddress = address(newToken);
        deployedContracts.push(deployedContractAddress);
    }
}

